      hv_Message = hv_Message.TupleConcat(hv_Text);
      hv_Message = hv_Message.TupleConcat(hv_CamParTable);
      hv_Message = hv_Message.TupleConcat(" ");
      hv_Message = hv_Message.TupleConcat(("Back projection error = "+(hv_Error.TupleString(
